mapper.select:
SELECT t.*, row_number() over(partition by bld_no, cell_no, floor_no order by lpad(floor_no, 10, '0'), show_name, house_id) init_room, SUM(CASE WHEN t.presell_state = '已售' or t.presell_state = '已受理' THEN 1 ELSE 0 END) over(partition BY bld_no, cell_name, floor_no) sale_num, SUM(CASE WHEN t.presell_state = '未售' and t.sales_flag = '是' THEN 1 ELSE 0 END) over(partition BY bld_no, cell_name, floor_no) no_sale_Num, SUM(CASE WHEN t.presell_state = '未售' and t.sales_flag != '是' THEN 1 ELSE 0 END) over(partition BY bld_no, cell_name, floor_no) no_saled_Num, SUM(1) over(partition BY bld_no, cell_name, floor_no) total_num FROM view_houseinfo t WHERE project_id = #{projectId} and bld_no = #{bldNo}
and cell_name = #{cellNo}
ORDER BY case when regexp_replace(t.floor_no,'^[-\+]?\d+(\.\d+)?$','') is not null then 9999 else to_number(floor_no) end DESC, show_name ASC, house_id ASC

SELECT house_id, show_name, house_hold, house_use, case when instr(t.cell_name, 'B') > 0 then t.cell_name when instr(t.bld_no, '地下') > 0 THEN 'B' || t.cell_name ELSE t.cell_name || '单元' END cell_name, floor_no, cell_name as cell_no, case when instr(floor_name, '层') > 0 THEN floor_name else floor_name || '层' end as floor_name, construct_area, own_area, CASE WHEN mortgage_flag = '是' THEN '抵押' WHEN close_flag = '是' THEN '查封' WHEN frozen_flag = '是' THEN '关闭' WHEN presell_state = '已售' THEN '已售' WHEN presell_state = '关闭' THEN '关闭' WHEN presell_state = '已受理' THEN '签订中' WHEN sales_flag = '否' THEN t.nosale_reason WHEN presell_state = '未售' THEN '未售' ELSE '其他' END presell_state, CASE WHEN mortgage_flag = '是' THEN 'ddb400' WHEN close_flag = '是' THEN 'dd6600' WHEN frozen_flag = '是' THEN 'dd8800' WHEN presell_state = '已售' THEN 'dd6a62' WHEN presell_state = '已受理' THEN '03c2dd' WHEN presell_state = '关闭' THEN '959595' WHEN sales_flag = '否' THEN '959595' WHEN presell_state = '未售' THEN '00dd02' ELSE '00dd02' END color_code, CASE WHEN presell_state = '已售' or presell_state = '已受理' or sales_flag = '否' or frozen_flag = '是' THEN 0 ELSE presell_price END presell_price FROM view_houseinfo t WHERE project_id = #{projectId} AND bld_no = #{bldNo} AND cell_name = #{cellNo}
AND floor_no = #{floorNo}
ORDER BY case when instr(t.floor_no, '阁楼') > 0 or instr(t.floor_no, '顶') > 0 then 9999 when regexp_replace(t.floor_no,'^[-\+]?\d+(\.\d+)?$','') is not null then -100 else to_number(t.floor_no) end DESC, show_name ASC, house_id ASC

SELECT t.project_id, t.bld_no, CASE WHEN instr(t2.bld_name, '#') > 0 THEN t2.bld_name else t2.bld_name || '#' end bld_name,
CASE WHEN instr(t2.bld_name, '#') > 0 THEN t2.bld_name else t2.bld_name || '#' end || case when instr(t.cell_name, 'B') > 0 then t.cell_name when instr(t2.bld_name, '地下') > 0 THEN 'B' || t.cell_name ELSE t.cell_name || '单元' END cell_name,
count(1) total_num, SUM(CASE WHEN presell_state = '已售' or presell_state = '已受理' then 1 else 0 end) sell_num, sum(case when sales_flag = '否' then 1 else 0 end) no_sale_num, SUM(case when mortgage_flag = '是' then 1 else 0 end) mortgage_num, SUM(case when close_flag = '是' then 1 else 0 end) close_num, SUM(case when frozen_flag = '是' then 1 else 0 end) frozen_num, sum(case when presell_state = '未售' and sales_flag = '是' then 1 else 0 end) no_saled_num FROM view_houseinfo t left join view_bldInfo t2 on t.project_id = t2.project_id and t.bld_no = t2.bld_no WHERE t.project_id = #{projectId} AND t.bld_no = #{bldNo}
AND t.cell_name = #{cellNo}
group by t.project_id, t.bld_no, t2.bld_name
, t.cell_name

SELECT t.*, CASE WHEN mortgage_flag = '是' THEN '抵押' WHEN close_flag = '是' THEN '查封' WHEN frozen_flag = '是' THEN '关闭' WHEN presell_state = '已售' THEN '已售' WHEN presell_state = '关闭' THEN '关闭' WHEN presell_state = '已受理' THEN '签订中' WHEN sales_flag = '否' THEN nosale_reason WHEN presell_state = '未售' THEN '未售' ELSE '其他' END presell_state_name, CASE WHEN mortgage_flag = '是' THEN 'ddb400' WHEN close_flag = '是' THEN 'dd6600' WHEN frozen_flag = '是' THEN 'dd8800' WHEN presell_state = '已受理' THEN '03c2dd' WHEN presell_state = '已售' THEN 'dd6a62' WHEN presell_state = '关闭' THEN '959595' WHEN sales_flag = '否' THEN '959595' WHEN presell_state = '未售' THEN '00dd02' ELSE '00dd02' END presell_state_code FROM view_houseinfo t WHERE t.project_id = #{projectId} AND t.bld_no = #{bldNo} AND t.floor_no = #{floorNo}
AND cell_name = #{cellNo}
order by case when instr(t.floor_no, '阁楼') > 0 or instr(t.floor_no, '顶') > 0 then 9999 when regexp_replace(t.floor_no,'^[-\+]?\d+(\.\d+)?$','') is not null then -100 else to_number(t.floor_no) end DESC, show_name ASC, house_id ASC
